mapper.select:
select count(*) as total from BC_GOODS where

	 bcg_key = #{param2} 
	 bcg_name like '%${param2}%'

select * from ( select rownum num, A.* from ( select * from bc_goods where
bcg_key = #{param4} bcg_name like '%${param4}%'
order by bcg_date desc, bcg_key desc ) A where rownum <= #{param2} ) B where B.num >= #{param1}

mapper.update:
update bc_goods 
	set bcg_price = #{param2}*(100-#{param4})/100,
	    bcg_info = #{param3}, bcg_discount = #{param4}, bcg_mdpick = #{param5} 
	where bcg_key = #{param1}
